CREATE TABLE `cases` (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `user_id` INTEGER REFERENCES `users`(`id`), "details" TEXT, `urgency` TEXT, `preliminary_impression` TEXT, `self_care_advice` TEXT, `time` INTEGER DEFAULT (unixepoch()));
CREATE TABLE "users" (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `firstname` TEXT, `lastname` TEXT, `middleinitial` TEXT, `birthdate` TEXT, `gender` TEXT, `email` TEXT UNIQUE, `phone` TEXT, `province` TEXT, `city` TEXT, `barangay` TEXT, `street` TEXT, `avatar` TEXT DEFAULT 'image.png', `session` TEXT, `time` INTEGER DEFAULT (unixepoch()), `height` NUMERIC, `weight` NUMERIC, `blood_type` TEXT, `allergies` TEXT, `chronic_conditions` TEXT, `immunization_status` TEXT, `major_illnesses` TEXT, `current_medications` TEXT, `family_medical_history` TEXT, `smoking_status` TEXT, `alcohol_use` TEXT, `physical_activity` TEXT, `dietary_habits` TEXT, `last_updated` INTEGER DEFAULT (unixepoch()));
CREATE TABLE "doctors" (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `fullname` TEXT, `email` TEXT, `phone` TEXT, `license_number` TEXT, `professional_title` TEXT, `years_of_experience` INTEGER, `description` TEXT, `consultation_fee` NUMERIC DEFAULT (50), `avatar` TEXT DEFAULT ('image.png'), `session` TEXT, `time` INTEGER DEFAULT (unixepoch()));
CREATE TABLE "timeslots" (`id` INTEGER PRIMARY KEY AUTOINCREMENT, `doctor_id` INTEGER REFERENCES `doctors`(`id`), "case_id" INTEGER REFERENCES `cases`(`id`), `start_time` INTEGER DEFAULT (unixepoch()), `end_time` INTEGER DEFAULT (unixepoch()), `time` INTEGER DEFAULT (unixepoch()));
